#!/bin/sh
# Workbench Table Data copy script
# 
# Execute this to copy table data from a source RDBMS to MySQL.
# Edit the options below to customize it. You will need to provide passwords, at least.
# 
# Source DB: Mysql@127.0.0.1:3306 (MySQL)
# Target DB: Mysql@177.11.48.142:3306


# Source and target DB passwords
arg_source_password=
arg_target_password=

if [ -z "$arg_source_password" ] && [ -z "$arg_target_password" ] ; then
    echo WARNING: Both source and target RDBMSes passwords are empty. You should edit this file to set them.
fi
arg_worker_count=2
# Uncomment the following options according to your needs

# Whether target tables should be truncated before copy
# arg_truncate_target=--truncate-target
# Enable debugging output
# arg_debug_output=--log-level=debug3

/usr/lib/mysql-workbench/wbcopytables --mysql-source="root@127.0.0.1:3306" --target="jessicad@177.11.48.142:3306" --source-password="$arg_source_password" --target-password="$arg_target_password" --thread-count=$arg_worker_count $arg_truncate_target $arg_debug_output --table '`loja`' '`oc_category_description`' '`loja`' '`oc_category_description`' '`category_id`, `language_id`, `name`, `description`, `meta_title`, `meta_description`, `meta_keyword`' --table '`loja`' '`oc_upload`' '`loja`' '`oc_upload`' '`upload_id`, `name`, `filename`, `code`, `date_added`' --table '`loja`' '`oc_filter_group_description`' '`loja`' '`oc_filter_group_description`' '`filter_group_id`, `language_id`, `name`' --table '`loja`' '`oc_product_to_download`' '`loja`' '`oc_product_to_download`' '`product_id`, `download_id`' --table '`loja`' '`oc_order_option`' '`loja`' '`oc_order_option`' '`order_option_id`, `order_id`, `order_product_id`, `product_option_id`, `product_option_value_id`, `name`, `value`, `type`' --table '`loja`' '`oc_customer`' '`loja`' '`oc_customer`' '`customer_id`, `customer_group_id`, `store_id`, `firstname`, `lastname`, `email`, `telephone`, `fax`, `password`, `salt`, `cart`, `wishlist`, `newsletter`, `address_id`, `custom_field`, `ip`, `status`, `approved`, `safe`, `token`, `date_added`' --table '`loja`' '`oc_weight_class_description`' '`loja`' '`oc_weight_class_description`' '`weight_class_id`, `language_id`, `title`, `unit`' --table '`loja`' '`oc_user_group`' '`loja`' '`oc_user_group`' '`user_group_id`, `name`, `permission`' --table '`loja`' '`oc_marketing`' '`loja`' '`oc_marketing`' '`marketing_id`, `name`, `description`, `code`, `clicks`, `date_added`' --table '`loja`' '`oc_country`' '`loja`' '`oc_country`' '`country_id`, `name`, `iso_code_2`, `iso_code_3`, `address_format`, `postcode_required`, `status`' --table '`loja`' '`oc_attribute`' '`loja`' '`oc_attribute`' '`attribute_id`, `attribute_group_id`, `sort_order`' --table '`loja`' '`oc_tax_rate_to_customer_group`' '`